import React, {Component} from 'react';
import axios from "axios";


class AddProperty extends Component {
  constructor(props) {
    super(props);
    this.state = {
      name: "",
      description: "",
      address: "",
      price: null,
      square: null,
      number_of_bedrooms: null,
      number_of_bathrooms: null,
      number_of_car_parking: null,
    };
  }

  onChange = (e) => {
    this.setState({ [e.target.name]: e.target.value });
  };

  onAddClick = (e) => {
    e.preventDefault();

    const property = {
      name: this.state.name,
      lastname: this.state.lastname,
      description: this.state.description,
      address: this.state.address,
      price: this.state.price,
      square: this.state.square,
      number_of_bedrooms: this.state.number_of_bedrooms,
      number_of_bathrooms: this.state.number_of_bathrooms,
      number_of_car_parking: this.state.number_of_car_parking,
    };

    axios
      .post("/api/properties/add", property)
      .then((res) => {
        this.setState({
          name: "",
          description: "",
          address: "",
          price: null,
          square: null,
          number_of_bedrooms: null,
          number_of_bathrooms: null,
          number_of_car_parking: null,
        });
        this.props.history.push("/");
      })
      .catch((err) => {
        console.log("Error in Inquiry!");
      });
  };

  render() {
    return (
      <>
        <section className="contact_form py-5">
          <div className="container">
            <div className="heading">
              <h1 className="heading text-capitalize mb-5 pb-3">
                <span>Add</span> Property
              </h1>
              <h4 className="position cont">Properties</h4>
            </div>
            <form noValidate onSubmit={this.onAddClick}>
              <div className="input-w3ls w3ls-left">
                <input
                  type="text"
                  value={this.state.name}
                  onChange={this.onChange}
                  className="name"
                  name="firstname"
                  id="firstname"
                  placeholder="First Name"
                  required
                />
              </div>
              <div className="input-w3ls w3ls-rght">
                <input
                  type="text"
                  value={this.state.lastname}
                  onChange={this.onChange}
                  className="name"
                  name="lastname"
                  placeholder="Last Name"
                  required
                />
              </div>
              <div className="input-w3ls w3ls-left">
                <input
                  type="email"
                  value={this.state.email}
                  onChange={this.onChange}
                  className="name"
                  name="email"
                  id="email"
                  placeholder="Email"
                  required
                />
              </div>
              <div className="input-w3ls">
                <textarea
                  placeholder="Your Message"
                  value={this.state.message}
                  onChange={this.onChange}
                  name="message"
                  id="message"
                  required
                  defaultValue={""}
                />
              </div>
              <div className="input-w3ls">
                <input type="submit" defaultValue="Send Message" />
              </div>
            </form>      
          </div>
        </section>
      </>
    )
  }
}

export default AddProperty;
