import React, {Component} from 'react';
import axios from "axios";

import './add-property.css'

class AddProperty extends Component {
  constructor(props) {
    super(props);
    this.state = {
      name: "",
      description: "",
      address: "",
      price: 0,
      square: 0,
      number_of_bedrooms: 0,
      number_of_bathrooms: 0,
      number_of_car_parking: 0,
    };
  }

  onChange = (e) => {
    this.setState({ [e.target.name]: e.target.value });
  };

  onAddClick = (e) => {
    e.preventDefault();

    const property = {
      name: this.state.name,
      lastname: this.state.lastname,
      description: this.state.description,
      address: this.state.address,
      price: this.state.price,
      square: this.state.square,
      number_of_bedrooms: this.state.number_of_bedrooms,
      number_of_bathrooms: this.state.number_of_bathrooms,
      number_of_car_parking: this.state.number_of_car_parking,
    };

    axios
      .post("/api/properties/add", property)
      .then((res) => {
        this.setState({
          name: "",
          description: "",
          address: "",
          price: 0,
          square: 0,
          number_of_bedrooms: 0,
          number_of_bathrooms: 0,
          number_of_car_parking: 0,
          valid: {
            price: false,
            square: false,
            number_of_bedrooms: false,
            number_of_bathrooms: false,
            number_of_car_parking: false
          },
          formValid: false
        })
      })
      .catch((err) => {
        console.log(err);
      });
  };

  validateField(field, value) {
    const data = [...this.state.valid];
    const priceValid = data.price;
    const bedroomsValid = data.number_of_bedrooms;
    const bathroomsValid = data.number_of_bathrooms;
    const carparkingValid = data.number_of_car_parking;
    const squareValid = data.square;

    switch (field) {
      case 'price':
        priceValid = value.length >= 0
        break;
      case 'number_of_bedrooms':
        bedroomsValid = value.length >= 0
        break;
      case 'number_of_bathrooms':
        bathroomsValid = value.length >= 0
        break;
      case 'number_of_car_parking':
        carparkingValid = value.length >= 0
        break;
      case 'square':
        squareValid = value.length >= 0
        break;
    }
    this.setState({
      valid: {
        price: priceValid,
        square: squareValid,
        number_of_bedrooms: bedroomsValid,
        number_of_bathrooms: bathroomsValid,
        number_of_car_parking: carparkingValid
      }
    }, this.validateForm)
  }

  validateForm() {
    const { price, square, number_of_bedrooms, number_of_bathrooms, number_of_car_parking } = this.state.valid;
    this.setState({formValid: price && square && number_of_bedrooms && number_of_bathrooms && number_of_car_parking});
  }

  onChange = (e) => {
    console.log(this.state)
    const { name, value } = e.target.value;
    this.setState({ [name]: value }, this.validateField(name, value));
  };

  render() {
    return (
      <>
        <section className="contact_form py-5">
          <div className="container">
            <div className="heading">
              <h1 className="heading text-capitalize mb-5 pb-3">
                <span>Добавить</span> Объект
              </h1>
              <h4 className="position cont">Объекты</h4>
            </div>
            <form noValidate onSubmit={this.onAddClick}>
              <div className="input-w3ls w3ls-left">
                <input
                  type="text"
                  value={this.state.name}
                  onChange={this.onChange}
                  className="name"
                  name="name"
                  id="name"
                  placeholder="Имя"
                  required
                />
              </div>
              <div className="input-w3ls w3ls-rght">
                <input
                  type="text"
                  value={this.state.address}
                  onChange={this.onChange}
                  className="name"
                  name="address"
                  placeholder="Адрес"
                  required
                />
              </div>
              <div className="input-w3ls w3ls-left">
                <span class="text-muted">Цена</span>
                <br/>
                <input
                  type="number"
                  value={this.state.price}
                  onChange={this.onChange}
                  className="number"
                  name="price"
                  id="price"
                  placeholder="цена"
                  min="1"
                  required
                />
              </div>
              <div className="input-w3ls w3ls-rght">
                <span class="text-muted">Количество спален</span>
                <br/>
                <input
                  type="number"
                  value={this.state.number_of_bedrooms}
                  onChange={this.onChange}
                  className="number"
                  name="number_of_bedrooms"
                  id="number_of_bedrooms"
                  placeholder="кол-во спален"
                  min="1"
                  required
                />
              </div>
              <div className="input-w3ls w3ls-left">
                <span class="text-muted">Количество парковочных мест</span>
                <br/>
                <input
                  type="number"
                  value={this.state.number_of_car_parking}
                  onChange={this.onChange}
                  className="number"
                  name="number_of_car_parking"
                  id="number_of_car_parking"
                  placeholder="парковочные места"
                  min="1"
                  required
                />
              </div>
              <div className="input-w3ls w3ls-rght">
                <span class="text-muted">Количество ванных комнат</span>
                <br/>
                <input
                  type="number"
                  value={this.state.number_of_bathrooms}
                  onChange={this.onChange}
                  className="number"
                  name="number_of_bathrooms"
                  id="number_of_bathrooms"
                  placeholder="Количество ванных комнат"
                  min="1"
                  required
                />
              </div>
              <div className="input-w3ls w3ls-left">
                <span class="text-muted">Площадь</span>
                <br/>
                <input
                  type="number"
                  value={this.state.square}
                  onChange={this.onChange}
                  className="number"
                  name="square"
                  id="square"
                  placeholder="Площадь"
                  min="1"
                  required
                />
              </div>
              <div className="input-w3ls">
                <textarea
                  placeholder="Описание"
                  value={this.state.description}
                  onChange={this.onChange}
                  name="description"
                  id="description"
                  required
                  defaultValue={""}
                />
              </div>
              <div className="input-w3ls">
                <input type="submit" defaultValue="Send Message" value="Подтвердить" />
              </div>
            </form>      
          </div>
        </section>
      </>
    )
  }
}

export default AddProperty;
